mapper.select:
select 

from BILL_OM_DELIVER_DTL
where LOCNO = #{locno,jdbcType=VARCHAR}
  and OWNER_NO = #{ownerNo,jdbcType=VARCHAR}
  and DELIVER_NO = #{deliverNo,jdbcType=VARCHAR}
  and CONTAINER_NO = #{containerNo,jdbcType=VARCHAR}
  and CONTAINER_ID = #{containerId,jdbcType=DECIMAL}

select B.* from (select A.*, rownum rn from( select d.LOCNO, d.OWNER_NO, d.DELIVER_NO, d.BOX_NO, d.Store_No, d.ITEM_NO, d.SIZE_NO, max(d.creator) creator, max(d.creatorname) creatorname, max(d.editor) editor, max(d.editorname) editorname, MAX(i1.item_name) as item_name, MAX(s1.store_name) as store_name, s1.CIRCLE_NO, (select c.CIRCLE_NAME from BM_CIRCLE c where c.CIRCLE_NO = s1.CIRCLE_NO and c.locno = d.locno and rownum = 1) as CIRCLE, sum(d.qty) qty, d.LOADPROPOSE_NO, (select lp.TMS_DELIVER_NO from BILL_OM_LOADPROPOSE lp where lp.locno = d.locno and lp.LOADPROPOSE_NO = d.LOADPROPOSE_NO) as tmsDeliverNo from BILL_OM_DELIVER_DTL d LEFT join item i1 on i1.item_no = d.item_no LEFT join store s1 on s1.store_no = d.Store_No where 1 = 1
group by d.LOCNO, d.OWNER_NO, d.DELIVER_NO, d.BOX_NO, d.Store_No, d.ITEM_NO, d.SIZE_NO, s1.CIRCLE_NO, d.LOADPROPOSE_NO
order by ${orderByField}
${orderBy}
order by d.BOX_NO, d.ITEM_NO, d.SIZE_NO
) A where rownum < #{page.endRowNum}) B where rn > #{page.startRowNum}

