mapper.select:
select NVL(Max(t.container_id), 0) as container_id
 from BILL_OM_DELIVER_DTL t
 where t.locno = #{locno,jdbcType=VARCHAR}
 and t.deliver_no = #{deliverNo,jdbcType=VARCHAR}
 and t.owner_no = #{ownerNo,jdbcType=VARCHAR}
 and t.container_no = #{containerNo,jdbcType=VARCHAR}

select count(1) count
 from BILL_OM_DELIVER_DTL t
 where 1 = 1
 and t.locno = #{locno,jdbcType=VARCHAR}
 and t.deliver_no = #{deliverNo,jdbcType=VARCHAR}
 and t.owner_no = #{ownerNo,jdbcType=VARCHAR}
 and t.container_no = #{containerNo,jdbcType=VARCHAR}

mapper.sql:
select tt1.recheck_no, tt1.container_no, tt1.box_no, tt1.store_no, sum(tt1.qty) as qty, t2.store_name from (select tt2.recheck_no, t1.locno, t1.container_no, t1.box_no, t1.item_no, t1.store_no, t1.size_no, t1.qty from bill_om_deliver_dtl t1 inner join bill_om_deliver t2 on t2.locno = t1.locno and t2.deliver_no = t1.deliver_no
left join (select rd.locno, rd.container_no, rd.recheck_no from bill_om_recheck_dtl rd group by rd.locno, rd.container_no, rd.recheck_no) tt2 on t1.locno = tt2.locno and t1.container_no = tt2.container_no
where 1 = 1
and t1.locno = #{params.locno,jdbcType=VARCHAR}
and t1.deliver_no = #{params.deliverNo,jdbcType=VARCHAR}
and t1.owner_no = #{params.ownerNo,jdbcType=VARCHAR}
and t1.brand_no in
) tt1, store t2 where tt1.store_no = t2.store_no group by tt1.recheck_no,tt1.container_no, tt1.box_no, tt1.store_no, t2.store_name order by tt1.recheck_no, tt1.container_no, tt1.box_no, tt1.store_no

select tt1.loadpropose_no,tt1.tms_deliver_no, tt1.container_no, tt1.box_no, tt1.store_no, sum(tt1.qty) as qty, t2.store_name from (select tt2.loadpropose_no, lp.tms_deliver_no, t1.locno, t1.container_no, t1.box_no, t1.item_no, t1.store_no, t1.size_no, t1.qty from bill_om_deliver_dtl t1 inner join bill_om_deliver t2 on t2.locno = t1.locno and t2.deliver_no = t1.deliver_no left join bill_om_loadpropose_dtl tt2 on t1.locno = tt2.locno and t1.container_no = tt2.container_no left join bill_om_loadpropose lp on lp.locno=t1.locno and lp.loadpropose_no=t1.loadpropose_no where 1 = 1
and t1.locno = #{params.locno,jdbcType=VARCHAR}
and t1.deliver_no = #{params.deliverNo,jdbcType=VARCHAR}
and t1.owner_no = #{params.ownerNo,jdbcType=VARCHAR}
and t1.brand_no in
) tt1, store t2 where tt1.store_no = t2.store_no group by tt1.loadpropose_no, tt1.tms_deliver_no, tt1.container_no, tt1.box_no, tt1.store_no, t2.store_name order by tt1.loadpropose_no, tt1.container_no, tt1.box_no, tt1.store_no

