if.if:
and EXISTS (
	select 1 from BILL_IM_IMPORT_DTL d,item i
		where d.LOCNO = m.LOCNO 
		and d.IMPORT_NO = m.IMPORT_NO 
		and d.ITEM_NO = i.ITEM_NO

			and i.BRAND_NO = #{params.brandNo,jdbcType=VARCHAR}


			and d.deliver_no = #{params.deliverNo,jdbcType=VARCHAR}


			and d.box_no = #{params.boxNo,jdbcType=VARCHAR}


		and rownum=1
)

mapper.select:
select 

from BILL_IM_IMPORT m
where IMPORT_NO = #{importNo,jdbcType=VARCHAR}
  and LOCNO = #{locno,jdbcType=VARCHAR}
  and OWNER_NO = #{ownerNo,jdbcType=VARCHAR}

mapper.sql:
left join (select locno, owner_no, import_no, sum(nvl(receipt_Qty, 0)) receipt_Qty, sum(nvl(IMPORT_QTY, 0)) IMPORT_QTY, sum(abs(nvl(receipt_Qty, 0) - nvl(IMPORT_QTY, 0))) differQty from BILL_IM_IMPORT_DTL where locno = #{params.locno,jdbcType=VARCHAR}
and brand_no in
group by locno, owner_no, import_no ) sub1 on sub1.locno = m.locno and sub1.owner_no = m.owner_no and sub1.import_no = m.import_no left join (select locno, owner_no, import_no from BILL_IM_IMPORT_DTL where locno = #{params.locno,jdbcType=VARCHAR} group by locno, owner_no, import_no ) sub2 on sub2.locno = m.locno and sub2.owner_no = m.owner_no and sub2.import_no = m.import_no

mapper.select:
select , (select max(d.trans_time) from bill_im_import_dtl d where d.locno = vc.locno and vc.owner_no = d.owner_no and vc.import_no = d.import_no) transTime, (CASE WHEN vc.BUSINESS_TYPE = '0' THEN (select supplier_name from SUPPLIER s where s.supplier_no = vc.supplier_no) WHEN vc.BUSINESS_TYPE = '4' then (select store_name from store s where s.store_no = vc.supplier_no) END) AS supplierName, nvl((select sum(nvl(d.po_Qty, 0)) from bill_im_import_dtl d where d.locno = vc.locno and vc.owner_no = vc.owner_no and vc.import_no = d.import_no),0) sumQty, (SELECT COUNT(DISTINCT box_no) FROM bill_im_import_dtl d WHERE d.import_no = vc.import_no ) boxNoQty, (SELECT COUNT(DISTINCT box_no) FROM bill_im_import_dtl d WHERE d.import_no = vc.import_no and d.receipt_qty!=0 ) receiptBoxNoQty, vc.receipt_Qty, vc.IMPORT_QTY, vc.differQty from ( select B.* from ( select A.*,rownum rn from(
order by ${orderByField}
${orderBy}
order by m.IMPORT_NO desc
) A where rownum < #{page.endRowNum} ) B where rn > #{page.startRowNum} )vc

    select count(*) from bill_im_import i
    inner join bill_im_receipt_dtl rd on i.import_no=rd.import_no and i.locno=rd.locno and i.owner_no = rd.owner_no 
	inner join bill_im_receipt r on rd.receipt_no=r.receipt_no and rd.locno=r.locno and rd.owner_no=r.owner_no 
	where i.import_no=#{importNo,jdbcType=VARCHAR} 
	and i.locno=#{locno,jdbcType=VARCHAR} 
	and i.owner_no=#{ownerNo,jdbcType=VARCHAR} 
	and r.status in ('10','20')  
	and r.business_type = '0'

    select count(*) from bill_im_import i 
	inner join bill_im_receipt_dtl rd on i.import_no=rd.import_no and i.locno=rd.locno and i.owner_no = rd.owner_no
	inner join bill_im_receipt r on rd.receipt_no=r.receipt_no and rd.locno=r.locno and rd.owner_no=r.owner_no 
	inner join bill_om_divide_dtl dd on r.receipt_no=dd.source_no and dd.locno=r.locno and dd.owner_no=r.owner_no 
	inner join bill_om_divide d on d.divide_no=dd.divide_no and dd.locno=d.locno
	where i.import_no=#{importNo,jdbcType=VARCHAR} 
	and i.locno=#{locno,jdbcType=VARCHAR} 
	and i.owner_no=#{ownerNo,jdbcType=VARCHAR} 
	and d.status not in ('45','91')
	and r.business_type = '0'

    select count(*) from bill_im_import i
	  inner join bill_im_receipt_dtl rd on i.import_no=rd.import_no and i.locno=rd.locno and i.owner_no = rd.owner_no 
	  inner join bill_im_receipt r on rd.receipt_no=r.receipt_no and rd.locno=r.locno and rd.owner_no=r.owner_no   
	  inner join bill_im_check c on r.receipt_no=c.s_import_no and c.locno=r.locno and c.owner_no=r.owner_no 
	  where i.import_no=#{importNo,jdbcType=VARCHAR} 
	  and i.locno=#{locno,jdbcType=VARCHAR} 
	  and i.owner_no=#{ownerNo,jdbcType=VARCHAR} 
	  and r.status ='50' and c.status='10'
	  and r.business_type = '0'

select B.* from (select A.*,rownum rn from( select distinct b.LOCNO, b.IMPORT_NO, b.TRANS_NO, b.OWNER_NO, de.OWNER_NAME, b.ORDER_DATE, b.REQUEST_DATE, b.po_no, b.sys_no, ld.itemname as sysName, b.supplier_no, (CASE WHEN b.BUSINESS_TYPE = '0' THEN (select supplier_name from SUPPLIER s where s.supplier_no = b.supplier_no and rownum=1) WHEN b.BUSINESS_TYPE = '4' then (select store_name from store s where s.store_no = b.supplier_no and rownum=1) END) AS supplierName, b.s_po_no from BILL_IM_IMPORT b left outer join BILL_IM_IMPORT_DTL d on b.IMPORT_NO = d.IMPORT_NO and b.LOCNO=d.LOCNO left outer join con_box cb on cb.locno=d.locno and d.box_no=cb.box_no left outer join con_box_dtl cbd on cb.locno=cbd.locno and cbd.box_no=cb.box_no left outer join item i on i.item_no = d.item_no left outer join brand br on i.brand_no = br.brand_no left outer join lookupdtl ld on ld.itemval = b.sys_no and ld.lookupcode = 'SYS_NO' and ld.systemid='21' left outer join supplier s on b.supplier_no = s.supplier_no left outer join bm_defowner de on de.OWNER_NO = b.OWNER_NO where 1=1 and cb.status='0' and cbd.import_no!=' '
and (b.status='11' or b.status='12' or b.status='30')
order by b.LOCNO, b.IMPORT_NO ) A where rownum < #{page.endRowNum}) B where rn > #{page.startRowNum}

mapper.delete:
delete from BILL_IM_IMPORT
