  and LOCNO = #{locno,jdbcType=VARCHAR}
  and OWNER_NO = #{ownerNo,jdbcType=VARCHAR}

delete from BILL_IM_IMPORT
where IMPORT_NO = #{importNo,jdbcType=VARCHAR}
  and LOCNO = #{locno,jdbcType=VARCHAR}
  and OWNER_NO = #{ownerNo,jdbcType=VARCHAR}
  and STATUS = #{status,jdbcType=VARCHAR}
